Select inst.nomInstallation, inst.adresse, inst.code_postal from installations inst where ville = "cholet" and inst.numeroInstallation in(Select equip.numeroInstallation_activite from equipement equip, equipements_Assoc_activites equipActivite where equip.idEquipement=equipActivite.idEquipement_Activite and equipActivite.codeActivite in (select codeActivite from activite where libeleActivite="Vol Ã  voile"))
Select i.nom, i.numero from installation i JOIN equipement e on i.numero=e.numeroInstallation JOIN equipements_Assoc_activites ea on e.numero=ea.numeroEquipement JOIN activite a on a.numero=ea.numeroActivite where ville like 'Nantes'and a.nom like '%Basket%'
Select i.nomInstallation, i.numeroInstallation from installations i JOIN equipement e on i.numeroInstallation=e.numeroInstallation_activite JOIN equipements_Assoc_activites ea on e.idEquipement=ea.idEquipement_Activite JOIN activite a on a.codeActivite=ea.codeActivite where i.ville= 'Nantes 'and a.libeleActivite like 'Saut'